  "content": ""use client";

import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Sparkles, Globe, Loader2 } from "lucide-react";

interface StoryGenerationDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onGenerate: (prompt: string, language: string) => Promise<void>;
  isLoading: boolean;
  isLoggedIn: boolean;
  onLogin: () => void;
}

export function StoryGenerationDialog({
  open,
  onOpenChange,
  onGenerate,
  isLoading,
  isLoggedIn,
  onLogin,
}: StoryGenerationDialogProps) {
  const [prompt, setPrompt] = useState("");
  const [language, setLanguage] = useState<"chinese" | "english">("chinese");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!prompt.trim()) return;

    if (!isLoggedIn) {
      onLogin();
      return;
    }

    await onGenerate(prompt.trim(), language);
    setPrompt("");
    onOpenChange(false);
  };

  const languageOptions = [
    { value: "chinese", label: "中文", icon: "🇨🇳" },
    { value: "english", label: "English", icon: "🇺🇸" },
  ];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader className="space-y-3">
          <DialogTitle className="flex items-center gap-2 text-xl">
            <Sparkles className="h-5 w-5 text-primary" />
            生成你的故事
          </DialogTitle>
          <DialogDescription className="text-base">
            输入故事情节，选择语言，让AI为你创作一个精彩的故事。
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="story-prompt" className="text-sm font-medium">
              故事情节
            </Label>
            <Textarea
              id="story-prompt"
              placeholder="例如：一只企鹅梦想着飞翔..."
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              disabled={isLoading}
              className="min-h-[100px] resize-none"
              maxLength={500}
            />
            <div className="flex justify-between items-center text-xs text-muted-foreground">
              <span>描述你想要的故事场景、角色或情节</span>
              <span>{prompt.length}/500</span>
            </div>
          </div>

          <div className="space-y-2">
            <Label className="text-sm font-medium flex items-center gap-2">
              <Globe className="h-4 w-4" />
              故事语言
            </Label>
            <Select value={language} onValueChange={(value: "chinese" | "english") => setLanguage(value)}>
              <SelectTrigger className="w-full">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {languageOptions.map((option) => (
                  <SelectItem key={option.value} value={option.value}>
                    <div className="flex items-center gap-2">
                      <span>{option.icon}</span>
                      <span>{option.label}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {!isLoggedIn && (
            <div className="bg-amber-50 dark:bg-amber-950 border border-amber-200 dark:border-amber-800 rounded-lg p-4">
              <div className="flex items-start gap-3">
                <div className="flex-shrink-0 w-5 h-5 bg-amber-100 dark:bg-amber-900 rounded-full flex items-center justify-center mt-0.5">
                  <span className="text-amber-600 dark:text-amber-400 text-xs">!</span>
                </div>
                <div className="text-sm">
                  <p className="text-amber-800 dark:text-amber-200 font-medium">
                    需要登录才能生成故事
                  </p>
                  <p className="text-amber-700 dark:text-amber-300 mt-1">
                    登录后可以保存你的故事创作历史，并享受更好的服务体验。
                  </p>
                </div>
              </div>
            </div>
          )}

          <DialogFooter className="flex gap-3 sm:gap-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              disabled={isLoading}
              className="flex-1 sm:flex-none"
            >
              取消
            </Button>
            <Button
              type="submit"
              disabled={isLoading || !prompt.trim()}
              className="flex-1 sm:flex-none"
            >
              {isLoading ? (
                <>
                  <Loader2 className="h-4 w-4 animate-spin mr-2" />
                  生成中...
                </>
              ) : !isLoggedIn ? (
                <>
                  <Sparkles className="h-4 w-4 mr-2" />
                  登录并生成
                </>
              ) : (
                <>
                  <Sparkles className="h-4 w-4 mr-2" />
                  生成故事
                </>
              )}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}"
}

